! function(document, window, $) {
    "use strict",
        function() {
            $("#pageRowToggler").footable()
        }(),
        function() {
            var fooColExp = $("#pageAccordion");
            fooColExp.footable().trigger("footable_expand_first_row"), $("#pageCollapseBtn").on("click", function() {
                fooColExp.trigger("footable_collapse_all")
            }), $("#pageExpandBtn").on("click", function() {
                fooColExp.trigger("footable_expand_all")
            })
        }(),
        function() {
            $("#pagePagination").footable(), $("#pageShow").change(function(e) {
                e.preventDefault();
                var pagesize = $(this).find("option:selected").val();
                $("#pagePagination").data("page-size", pagesize), $("#pagePagination").trigger("footable_initialized")
            })
        }(),
        function() {
            var filtering = $("#pageFootableFiltering");
            filtering.footable().on("footable_filtering", function(e) {
                var selected = $("#pagefilteringStatus").find(":selected").val();
                e.filter += e.filter && e.filter.length > 0 ? " " + selected : selected, e.clear = !e.filter
            }), $("#pagefilteringStatus").change(function(e) {
                e.preventDefault(), filtering.trigger("footable_filter", {
                    filter: $(this).val()
                })
            }), $("#pagefilteringSearch").on("input", function(e) {
                e.preventDefault(), filtering.trigger("footable_filter", {
                    filter: $(this).val()
                })
            })
        }(),
        function() {
            var addrow = $("#pageFooable");
            addrow.footable();
        }()
}(document, window, jQuery);