!function (window, document, $) {
    "use strict";
    $(document).on("click.panel.transition", "[data-type]", function () {
        var $this = $(this);
        var type = $this.attr("data-type");
        $('.panel').addClass('animation-' + type);
        setTimeout(function ()
        {
            $(".panel").removeClass('animation-' + type);
        }, 700);
    });
}(window, document, jQuery);